import { useState } from "react";
import "./desktop3.css";
import { getDownloadURL } from "firebase/storage";
const initialState={
  name: "",
  specialization: "",
  city: "",
  des: ""
}
const Desktop3 = () => {
  const[data,setData]=useState(initialState);
  const{name,specialization,city,des}=data;
  const[file,setFile]=useState(null);
  const[progress,setprogress]=useState(null);

  useEffect(()=>{
    const uploadFile=()=>{
      const name = new Date().getTime()+file.name;
      const storageRef = ref(storage,file.name);
      const uploadTask = uploadBytesResumable(storageRef,file);

      uploadTask.on("state_changed",(snapshot)=>{
        const progress=
        (snapshot.bytesTransferred / snapshot.totalBytes)*100;
        setprogress(progress);
      },(error)=>{
        console.log(error)
      },
      ()=>{
        getDownloadURL(uploadTask.snapshot.ref).then((downloadURL)=>{
          setData((prev)=>({...prev,img:downloadURL}));
        })
      });
    };
  },[file]);


const handleChange = (e)=>{
  setData({...data, [e.target.name]:e.target.vaue})
};

const handleSubmit = (e) =>{
  e.preventDefault();
  if(!(name&&specialization&&city&&des))
  {
    alert("Please enter all the details.");
  }
}

  return (
    <div className="desktop-3">
      <div className="desktop-3-child">
        <form onSubmit={handleSubmit}>
          <div className="upload-image">
            <input
              className="rectangle-input"
              type="file"
              accept="image/*"
              onChange={(e)=> setFile(e.target.files[0])}
            />
          </div>

          <input
            className="rectangle-input"
            placeholder="Enter specialization"
            type="text"
            name="specialization"
            value={specialization}
            onChange={handleChange}
          />
          <input
            className="rectangle-input"
            placeholder="Enter name"
            type="text"
            name="name"
            value={name}
            onChange={handleChange}
          />
          {/* <div className="rectangle-div" /> */}
          <input
            className="rectangle-input"
            placeholder="Enter city"
            type="text"
            name="city"
            value={city}
            onChange={handleChange}
          />
          <textarea
            className="rectangle-input rectangle-textarea"
            placeholder="Description"
            name="des"
            value={des}
            onChange={handleChange}
          />
          <br />

          <button
            type="submit" className="rectangle-input rectangle-button"
            disabled={progress!==null && progress<100}
            >Submit
          </button>

        </form>
      </div>
    </div>
  );
};

export default Desktop3;
