    setUserData({ ...Doctor, [name]: value })
  };

  // connect with firebase
  const submitData = async (event) => {
    event.preventDefault();
    const { name, image, specializaion, city, des } = Doctor
    const res = await fetch("https://doctorwebsite-d352a-default-rtdb.firebaseio.com/doctorRecords.json",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
