        })
        alert("Data Stored");
      }
      else{
        alert("Async Error");
      }

  };



  return (
    <div className="desktop-3">
      <div className="desktop-3-child">
        <form method="POST">
          <div className="upload-image">
            <input
              className="rectangle-input"
              type="file"
              name="image"
              accept="image/*"
              value={Doctor.image}
              onChange={postData}
            />
          </div>

          <input
            className="rectangle-input"
            placeholder="Enter Specializaion"
            type="text"
            name="specialization"
            value={Doctor.specializaion}
            onChange={postData}
          />
          <input
            className="rectangle-input"
            placeholder="Enter name"
            type="text"
            name="name"
            value={Doctor.name}
            onChange={postData}
          />
          {/* <div className="rectangle-div" /> */}
          <input
            className="rectangle-input"
            placeholder="Enter city"
            type="text"
            name="city"
            value={Doctor.city}
            onChange={postData}
          />
          <textarea
            className="rectangle-input rectangle-textarea"
            placeholder="Description"
            name="des"
            value={Doctor.des}
            onChange={postData}
          />
          <br />

          <button
            type="submit" className="rectangle-input rectangle-button"
            onClick={submitData}>Submit
          </button>

        </form>
      </div>
    </div>
  );
};

export default Desktop3;
