import { useState } from "react";
import "./desktop3.css";
import { dataRef } from "./Firebase/Firebase";

const Desktop3 = () => {
  const [rectangleInputValue, setRectangleInputValue] = useState("");
  const [rectangleInput1Value, setRectangleInput1Value] = useState("");
  const [rectangleInput2Value, setRectangleInput2Value] = useState("");
  const [rectangleInput3Value, setRectangleInput3Value] = useState("");
  const [rectangleInput4Value, setRectangleInput4Value] = useState("");

  const handleAdd = () => {
    // handle database
    // specification  name  city  desp  image
    if (
      setRectangleInputValue != "" &&
      setRectangleInput1Value != "" &&
      setRectangleInput2Value != "" &&
      setRectangleInput3Value != "" &&
      setRectangleInput4Value != ""
    ) {
      dataRef
        .ref()
        .child("Doctor")
        .push(
          rectangleInputValue,
          rectangleInput1Value,
          rectangleInput3Value,
          rectangleInput4Value,
          rectangleInput2Value
        );
      alert("Data Stored");
      // empty the value
      setRectangleInputValue("");
      setRectangleInput1Value("");
      setRectangleInput2Value("");
      setRectangleInput3Value("");
      setRectangleInput4Value("");
    } else alert("Please Enter All Details.");
  };

  return (
    <div className="desktop-3">
      <div className="desktop-3-child">
        <form method="POST">
          <div className="upload-image">
            <input
              className="rectangle-input"
              type="file"
              name="image"
              accept="image/*"
              value={Doctor.image}
              onChange={postData}
            />
          </div>

          <input
            className="rectangle-input"
            placeholder="Enter Specializaion"
            type="text"
            name="specialization"
            value={Doctor.specializaion}
            onChange={postData}
          />
          <input
            className="rectangle-input"
            placeholder="Enter name"
            type="text"
            name="name"
            value={Doctor.name}
            onChange={postData}
          />
          {/* <div className="rectangle-div" /> */}
          <input
            className="rectangle-input"
            placeholder="Enter city"
            type="text"
            name="city"
            value={Doctor.city}
            onChange={postData}
          />
          <textarea
            className="rectangle-input rectangle-textarea"
            placeholder="Description"
            name="des"
            value={Doctor.des}
            onChange={postData}
          />
          <br />

          <input></input>
          <button
            className="rectangle-input rectangle-button"
            onClick={handleAdd}
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default Desktop3;
