import { useState } from "react";
import "./desktop3.css";
import { dataRef } from "./Firebase/Firebase";

const Desktop3 = () => {
  const [rectangleInputValue, setRectangleInputValue] = useState("");
  const [rectangleInput1Value, setRectangleInput1Value] = useState("");
  const [rectangleInput2Value, setRectangleInput2Value] = useState("");
  const [rectangleInput3Value, setRectangleInput3Value] = useState("");
  const [rectangleInput4Value, setRectangleInput4Value] = useState("");

  const handleAdd=()=>{
    // handle database
    // specification  name  city  desp  image
    if(setRectangleInputValue!=""&&setRectangleInput1Value!=""&&setRectangleInput2Value!=""&&setRectangleInput3Value&&setRectangleInput4Value)
    {
    dataRef.ref().child("Doctor").push(rectangleInputValue,rectangleInput1Value,rectangleInput3Value,rectangleInput4Value,rectangleInput2Value)
      alert("Data Stored");
    // empty the value
    setRectangleInputValue("")
    setRectangleInput1Value("")
    setRectangleInput2Value("")
    setRectangleInput3Value("")
    setRectangleInput4Value("")
  }
  else
  alert("Please Enter All Details.");
}

  return (
    <div className="desktop-3">
      <div className="desktop-3-child" >

<form>
<div className="upload-image">
      <input
        className="rectangle-input"
        placeholder="lkjghfhjk"
        type="file" accept="image/*"
        value={rectangleInput2Value}
        onChange={(event) => setRectangleInput2Value(event.target.value)}
      /> 
      </div>

      <input
        className="rectangle-input"
        placeholder="Enter Specializaion"
        type="text"
        value={rectangleInputValue}
        onChange={(event) => setRectangleInputValue(event.target.value)}
      />
      <input
        className="rectangle-input"
        placeholder="Enter name"
        type="text"
        value={rectangleInput1Value}
        onChange={(event) => setRectangleInput1Value(event.target.value)}
      />
      {/* <div className="rectangle-div" /> */}
      <input
        className="rectangle-input"
        placeholder="Enter city"
        type="text"
        value={rectangleInput3Value}
        onChange={(event) => setRectangleInput3Value(event.target.value)}
      />
      <textarea className="rectangle-input rectangle-textarea" placeholder="Description" 
        value={rectangleInput4Value}
        onChange={(event) => setRectangleInput4Value(event.target.value)}
      /><br/>
      <button className="rectangle-input rectangle-button" onClick={handleAdd}>Submit</button>
</form>


     </div> 
    </div>
  );
};

export default Desktop3;
