import { useState } from "react";
import "./desktop3.css";
const initialState={
  name: "",
  specialization: "",
  city: "",
  des: ""
}
const Desktop3 = () => {
  const [Doctor, setData] = useState({
    name: "",
    image: "",
    specialization: "",
    city: "",
    des: ""
  });
  let name, value;
  const postData = () => {
    name = event.target.name;
    value = event.target.value;

    setData({ ...Doctor, [name]: value })
  };

  // connect with firebase
  const submitData = async (event) => {
    event.preventDefault();
    const { name, image, specialization, city, des } = Doctor

    if (name && image && specialization && city && des)
    {
// image insertion



// body insertion
      const res = await fetch("https://doctorwebsite-d352a-default-rtdb.firebaseio.com/doctorRecords.json",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify({
            name,
            image,
            specialization,
            city,
            des
          })
        }
      );

    if (res) {
      setData({
        name: "",
        image: "",
        specialization: "",
        city: "",
        des: ""
      })
      alert("Data Stored");
    }
    else {
      alert("Async Error");
    }
  }
  else{
    alert("Please enter all the details.")
  }

  };



  return (
    <div className="desktop-3">
      <div className="desktop-3-child">
        <form method="POST">
          <div className="upload-image">
            <input
              className="rectangle-input"
              type="file"
              name="image"
              accept="image/*"
              value={Doctor.image}
              onChange={postData}
            />
          </div>

          <input
            className="rectangle-input"
            placeholder="Enter specialization"
            type="text"
            name="specialization"
            value={Doctor.specialization}
            onChange={postData}
          />
          <input
            className="rectangle-input"
            placeholder="Enter name"
            type="text"
            name="name"
            value={Doctor.name}
            onChange={postData}
          />
          {/* <div className="rectangle-div" /> */}
          <input
            className="rectangle-input"
            placeholder="Enter city"
            type="text"
            name="city"
            value={Doctor.city}
            onChange={postData}
          />
          <textarea
            className="rectangle-input rectangle-textarea"
            placeholder="Description"
            name="des"
            value={Doctor.des}
            onChange={postData}
          />
          <br />

          <button
            type="submit" className="rectangle-input rectangle-button"
            onClick={submitData}>Submit
          </button>

        </form>
      </div>
    </div>
  );
};

export default Desktop3;
